select patients.first_name,patients.last_name,province_names.province_name from patients join province_names
 on patients.province_id =province_names.province_id
;

select count(*) as male_count, 
 (select count(*) as female_count FROM patients where gender='F') 
 FROM patients where gender='M';

select first_name,last_name,'Patient' AS role from patients
union all
select first_name,last_name,'Doctor' as role from doctors;

select allergies,count(*) as total_diagnosis from patients 
where allergies is NOT NULL 
group by allergies order by total_diagnosis desc;

select first_name,last_name,birth_date from patients
where birth_date like '197%'
order by birth_date asc;

SELECT CONCAT(upper(last_name),',',lower(first_name) )AS new_name_format
FROM patients
order by first_name desc;

select province_id,sum(height)as sum_height from patients
group by province_id
having sum_height>=7000;

select concat(patients.first_name, ' ',patients.last_name)as patient_name, admissions.diagnosis , concat(doctors.first_name
,' ', doctors.last_name) as  doctor_name from patients inner join admissions on
patients.patient_id = admissions.patient_id inner join doctors on doctors.doctor_id = admissions.attending_doctor_id;

SELECT DISTINCT p.patient_id, CONCAT(a.patient_id, LENGTH(p.last_name), year(p.birth_date)) AS temp_password
FROM patients p
         JOIN admissions a ON p.patient_id = a.patient_id;

SELECT CONCAT(ROUND(SUM(CASE WHEN gender = 'M' THEN 1 ELSE 0 END) * 100.0 / COUNT(*), 2), '%')
FROM patients;

SELECT ROUND(100 * AVG(gender = 'M'), 2) || '%' AS percent_of_male_patients
FROM patients;
